import React, { useReducer } from 'react';
import uuid from 'uuid';
import {
  ADD_CONTACT,
  DELETE_CONTACT,
  SET_CURRENT,
  CLEAR_CCURRENT,
  UPDATE_CONTACT,
  FILTER_CONTACTS,
  CLEAR_FILTER,
} from '../types';
import ContactContext from './contactContext';
import { contactReducer } from './contactReducer';
const ContactState = (props) => {
  const initialState = {
    contacts: [
      {
        type: 'professional',
        id: 1,
        name: 'Aryan Singh',
        email: 'aryan@gmail.com',
        phone: '851853',
      },
      {
        type: 'personal',
        id: 2,
        name: 'Ajay Sharma',
        email: 'ajay@gmail.com',
        phone: '8552',
      },
      {
        type: 'personal',
        id: 3,
        name: 'Abhishek Dhakad',
        email: 'ajay@gmail.com',
        phone: '8552',
      },
    ],
  };
  const { state, dispatch } = useReducer(contactReducer, initialState);

  return (
    <contactContext.Provider.Provider value={{ contact: state.contacts }}>
      {props.children}
    </contactContext.Provider.Provider>
  );
};

export default ContactState;
